mapper.select:
SELECT COUNT(*) FROM (
SELECT compan_id AS 'depart_id', '' AS 'depart_pid', 'company' AS 'compan_id', NAME AS 'name', CODE, priori, STATUS, remark ,'company' AS 'type' FROM sys_compan c UNION SELECT depart_id, depart_pid, compan_id, NAME, CODE, priori, STATUS, remark ,'depart' AS 'type' FROM sys_depart d UNION SELECT POSITI_ID AS 'depart_id', DEPART_ID AS 'depart_pid', 'positi' AS 'compan_id', NAME AS 'name', 'positicode' AS 'CODE', priori, STATUS, 'remark' AS 'remark' ,'positi' AS 'type' FROM sys_positi
) sys_company_depart
