select c.TABLE_SCHEMA,c.TABLE_NAME,c.COLUMN_NAME,c.COLUMN_TYPE,c.COLUMN_COMMENT from INFORMATION_SCHEMA.COLUMNS c

c.TABLE_SCHEMA in

