mapper.select:
SELECT p.login_name , p.login_pwd , p.name , p.gender , p.age , p.email , p.qq , p.mphone_no , p.tphone_no , p.extens_no , p.status , p.depart_id , p.staff_id , d.NAME AS 'departname',c.NAME AS 'companname', GROUP_CONCAT( sp.positi_id) as 'positi_id', GROUP_CONCAT( spi.name ) AS 'positiname'
FROM sys_staff p LEFT JOIN sys_staff_positi sp ON p.STAFF_ID=sp.STAFF_ID LEFT JOIN sys_positi spi ON sp.positi_id =spi.positi_id LEFT JOIN sys_depart d ON p.DEPART_ID=d.DEPART_ID LEFT JOIN sys_compan c ON c.COMPAN_ID=d.COMPAN_ID WHERE 1=1
and p.depart_id in
#{item}
and p.depart_id = #{departId,jdbcType=VARCHAR}
GROUP BY p.login_name , p.login_pwd , p.name , p.gender , p.age , p.email , p.qq , p.mphone_no , p.tphone_no , p.extens_no , p.status , p.depart_id , p.staff_id ,d.NAME,c.NAME

SELECT COUNT(1) FROM ( SELECT p.STAFF_ID FROM sys_staff p LEFT JOIN sys_staff_positi sp ON p.STAFF_ID=sp.STAFF_ID LEFT JOIN sys_positi spi ON sp.positi_id =spi.positi_id LEFT JOIN sys_depart d ON p.DEPART_ID=d.DEPART_ID LEFT JOIN sys_compan c ON c.COMPAN_ID=d.COMPAN_ID WHERE 1=1
and p.depart_id in
#{item}
and p.depart_id = #{departId,jdbcType=VARCHAR}
GROUP BY p.STAFF_ID ) t

 select  sf.staff_id,sf.person_id ,sf.positi_id    from  sys_staff sf where sf.person_id =  #{personId,jdbcType=VARCHAR}

 and sf.positi_id  = #{positiId,jdbcType=VARCHAR}


 and sf.depart_id  = #{departId,jdbcType=VARCHAR}

mapper.update:
update sys_staff
set  DEPART_ID = #{departId,jdbcType=VARCHAR} 
where STAFF_ID = #{staffId,jdbcType=VARCHAR}
